mapper.update:
Update Need_Skill
SET skill = #{skill}
WHERE Need_Skill_Id = #{needSkillId}

mapper.select:
SELECT skill
FROM Need_Skill
WHERE notice_id = #{noticeId}

SELECT notice_id
FROM Need_Skill
WHERE skill = #{skill}

SELECT notice_id
FROM Need_Skill
WHERE skill = #{skill} and notice_id = #{noticeId}
